import React, { useState, useEffect } from "react";
import "../style/SearchPage.css";
import "../index.css";

export default function SearchSong() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [error, setError] = useState("");
  const [term, setTerm] = useState("");
  const [apiRoute, setApiRoute] = useState("");
  const [songs, setSongs] = useState([]);
  const [artistName, setArtistName] = useState(""); // State for artist search input
  const [artists, setArtists] = useState([]); // State for fetched artists

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres");
        if (!response.ok) {
          throw new Error(`Failed to fetch genres. Status: ${response.status}`);
        }
        const data = await response.json();
        setGenres(data.rows);
      } catch (error) {
        console.error("Error fetching genres:", error);
        setError("Failed to load genres");
      }
    };

    fetchGenres();
  }, []);

  // Fetch songs when apiRoute changes
  useEffect(() => {
    if (!apiRoute) return; // Skip if apiRoute is not set

    const fetchSongs = async () => {
      try {
        const response = await fetch(apiRoute);
        if (!response.ok) {
          throw new Error(`Failed to fetch songs. Status: ${response.status}`);
        }
        const data = await response.json();
        setSongs(data);
      } catch (err) {
        console.error("Error fetching songs:", err);
        setError("Failed to load songs.");
      }
    };

    fetchSongs();
  }, [apiRoute]); // Run when apiRoute changes

  const handleSubmitSongs = (e) => {
    e.preventDefault();
    setError("");
    setSongs([]); // Clear previous results
    if (!selectedGenre) {
      setError("Please select a genre.");
      return;
    }
    if (!term) {
      setError("Please select a results limit.");
      return;
    }

    const encodedGenre = encodeURIComponent(selectedGenre);
    setApiRoute(`http://localhost:8080/genres/${encodedGenre}/tracks?limit=${term}`);
  };

  const handleSearchArtists = async (e) => {
    e.preventDefault();
    setError("");
    setArtists([]); // Clear previous artist results
    if (!artistName) {
      setError("Please enter an artist name.");
      return;
    }

    try {
      const encodedArtistName = encodeURIComponent(artistName);
      const response = await fetch(`http://localhost:8080/artists?name=${encodedArtistName}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch artists. Status: ${response.status}`);
      }
      const data = await response.json();
      setArtists(data);
    } catch (err) {
      console.error("Error fetching artists:", err);
      setError("Failed to load artists.");
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div>

      {/* Page Wrapper */}
      <div id="page-wrapper">
        {/* Search Form for Songs */}
        <form id="search-form" onSubmit={handleSubmitSongs}>
          <label htmlFor="genre-select"></label>
          {/* Dropdown for Genre Selection */}
          <select
            id="genre-select"
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            <option value="" disabled>
              Select a Genre
            </option>
            {Array.isArray(genres) &&
              genres.map((genre) => (
                <option key={genre.id} value={genre.genre}>
                  {genre.genre}
                </option>
              ))}
          </select>

          {/* Dropdown for Results Limit */}
          <select
            id="results-limit"
            value={term}
            onChange={(e) => setTerm(e.target.value)}
          >
            <option value="" disabled>
              Select results limit
            </option>
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="50">50</option>
            <option value="100">100</option>
          </select>

          {/* Search Button */}
          <button type="submit">Search Songs</button>
        </form>

        {/* Search Form for Artists */}
        <form id="artist-search-form" onSubmit={handleSearchArtists}>
          <input
            type="text"
            placeholder="Enter artist name"
            value={artistName}
            onChange={(e) => setArtistName(e.target.value)}
          />
          <button type="submit">Search Artists</button>
        </form>

        {/* Display Error Message */}
        {error && <div className="error-message">{error}</div>}

        {/* Songs Table */}
        {songs.length > 0 && (
          <table className="results-table">
            <thead>
              <tr>
                <th>Track ID</th>
                <th>Title</th>
                <th>URL</th>
                <th>Release Date</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song) => (
                <tr key={song.track_id}>
                  <td>{song.track_id}</td>
                  <td>{song.title}</td>
                  <td>
                    <a
                      href={song.track_url}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Listen
                    </a>
                  </td>
                  <td>{song.release_date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {/* Artists Table */}
        {artists.length > 0 && (
          <table className="results-table">
            <thead>
              <tr>
                <th>Artist ID</th>
                <th>Name</th>
                <th>Genre</th>
                <th>Popularity</th>
              </tr>
            </thead>
            <tbody>
              {artists.map((artist) => (
                <tr key={artist.id}>
                  <td>{artist.id}</td>
                  <td>{artist.name}</td>
                  <td>{artist.genre}</td>
                  <td>{artist.popularity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
