import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom"; // For navigation
import "../style/GenrePage.css";
import "../index.css";

export default function GenrePage() {
  const [songs, setSongs] = useState([]);
  const [genresByID, setGenresByID] = useState([]);
  const [genres, setGenres] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [artists, setArtists] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(""); // Dropdown for artist name
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const history = useHistory(); // For navigation
  const [genreError, setGenreError] = useState("");
  const [artistError, setArtistError] = useState("");

  // Function to fetch random songs from unique genres

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres");
        if (!response.ok) {
          throw new Error(`Failed to fetch genres. Status: ${response.status}`);
        }
        const data = await response.json();
        setGenres(data.rows); // Assuming genres are in `rows`
      } catch (error) {
        console.error("Error fetching genres:", error);
        setGenreError("Failed to load genres.");
      }
    };

    fetchGenres();
  }, []);

  // Function to fetch genres ordered by ID
  const fetchGenresByID = async () => {
    setError("");
    try {
      const response = await fetch(`http://localhost:8080/genres-by-id`);
      if (!response.ok) {
        throw new Error(`Failed to fetch genres. Status: ${response.status}`);
      }
      const data = await response.json();
      setGenresByID(data.slice(0, 5)); // Show top 5 genres by ID
    } catch (error) {
      console.error("Error fetching genres by ID:", error);
      setError("Failed to load genres by ID.");
    }
  };

  // Function to fetch artists for the dropdown

  const fetchArtists = async () => {
    try {
      const response = await fetch("http://localhost:8080/artists");
      if (!response.ok) {
        throw new Error(`Failed to fetch artists. Status: ${response.status}`);
      }
      const data = await response.json();

      // Assuming artists are in `rows`
      if (Array.isArray(data.rows)) {
        setArtists(data.rows);
      } else {
        throw new Error("Invalid response structure: Expected 'rows' to be an array");
      }
    } catch (error) {
      console.error("Error fetching artists:", error);
      // setArtistError("Failed to load artists.");
    }
  };

  // Function to fetch top genres for a specific artist
  const fetchTopGenresByArtist = async () => {
    if (!selectedArtist) {
      setError("Please select an artist.");
      return;
    }
    setError("");
    try {
      const response = await fetch(
        `http://localhost:8080/artists/${encodeURIComponent(selectedArtist)}/top-genres`
      );
      if (!response.ok) {
        throw new Error(`Failed to fetch top genres. Status: ${response.status}`);
      }
      const data = await response.json();
      setTopGenres(data);
    } catch (error) {
      console.error("Error fetching top genres for artist:", error);
      setError("Failed to load top genres for artist.");
    }
  };

  // Fetch data on initial page load
  useEffect(() => {
    fetchRandomSongs();
    fetchGenresByID();
    fetchArtists();
  }, []);

  // Navigate back to the main menu
  const handleBackToMenu = () => {
    history.push("/"); // Navigate back to main menu
  };

  return (
    <div id="main_content">
      {/* Sidebar for Title, Back Button, and Refresh Button */}
      <div id="sidebar">
        <h1>Your Song of the Day by Genre</h1>

        <button id="back_button" onClick={handleBackToMenu}>
          Back to Main Menu
        </button>

        <button id="refresh_button" onClick={fetchRandomSongs}>
          Refresh Songs
        </button>
      </div>

      {/* Main Content */}
      <div id="content">
        {/* Display Random Songs */}
        <div id="song_list">
          <h2>ðŸŽµ Random Songs</h2>
          {isLoading ? (
            <div className="loading-message">Loading songs...</div>
          ) : songs.length > 0 ? (
            songs.map((song, index) => (
              <div key={index} className="song-card">
                <h3>{song.title}</h3>
                <p>Genre: {song.genre_name}</p>
              </div>
            ))
          ) : (
            <div className="loading-message">No songs available</div>
          )}
        </div>

        {/* Display Genres Ordered by ID */}
        <div id="genres_by_id">
          <h2>ðŸ”¢ Genres by ID</h2>
          {genresByID.length > 0 ? (
            <ul>
              {genresByID.map((genre) => (
                <li key={genre.ID}>{genre.Genre}</li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">Loading genres by ID...</div>
          )}
        </div>

        {/* Fetch Top Genres for a Specific Artist */}
        <div id="top_genres">
          <h2>ðŸŽ¤ Top Genres for Artist</h2>

          <select
            id="artist-select"
            value={selectedArtist}
            onChange={(e) => setSelectedArtist(e.target.value)}
          >
            <option value="" disabled>
              Select an Artist
            </option>
            {Array.isArray(artists) &&
              artists.map((artist) => (
                <option key={artist.ID} value={artist.Artist}>
                  {artist.Artist}
                </option>
              ))}
          </select>

          <button onClick={fetchTopGenresByArtist}>Fetch Top Genres</button>

          {topGenres.length > 0 ? (
            <ul>
              {topGenres.map((genre, index) => (
                <li key={index}>
                  {genre.genre_name} ({genre.track_count} tracks)
                </li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">No genres found for this artist.</div>
          )}
        </div>
      </div>
    </div>
  );
}
