import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom"; // For navigation
import "../style/GenrePage.css";
import "../index.css";

export default function GenrePage() {
  const [topGenres, setTopGenres] = useState([]);
  const [artists, setArtists] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(""); // For the dropdown
  const [error, setError] = useState("");
  const [artistError, setArtistError] = useState("");
  const history = useHistory(); // For navigation

  // Fetch artists for the dropdown on mount
  useEffect(() => {
    const fetchArtists = async () => {
      try {
        const response = await fetch("http://localhost:8080/artists");
        if (!response.ok) {
          throw new Error(`Failed to fetch artists. Status: ${response.status}`);
        }
        const data = await response.json();

        // Assuming artists are in `rows`
        if (Array.isArray(data.rows)) {
          setArtists(data.rows);
        } else {
          throw new Error("Invalid response structure: Expected 'rows' to be an array");
        }
      } catch (error) {
        console.error("Error fetching artists:", error);
        setError("Failed to load artists.");
      }
    };

    fetchArtists();
  }, []);

  // Fetch top genres for the selected artist
  const fetchTopGenresByArtist = async (e) => {
    e.preventDefault();
    setArtistError("");
    setTopGenres([]);
    if (!selectedArtist) {
      setArtistError("Please select an artist.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8080/artists/${encodeURIComponent(selectedArtist)}/top-genres`
      );
      if (!response.ok) {
        throw new Error(`Failed to fetch top genres. Status: ${response.status}`);
      }
      const data = await response.json();
      setTopGenres(data.topGenres);
    } catch (error) {
      console.error("Error fetching top genres for artist:", error);
      setArtistError("Failed to load top genres for artist.");
    }
  };

  // Navigate back to the main menu
  const handleBackToMenu = () => {
    history.push("/"); // Navigate back to the main menu
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/sound">Sound Lab</a></li>
          <li><a href="http://localhost:3000/genre">GenrePage</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

      {/* Page Wrapper */}
      <div id="page-wrapper">
        <section id="top-genres">
          <h2>ðŸŽ¤ Top Genres for Artist</h2>
          <form onSubmit={fetchTopGenresByArtist}>
            <select
              id="artist-select"
              value={selectedArtist}
              onChange={(e) => setSelectedArtist(e.target.value)}
            >
              <option value="" disabled>
                Select an Artist
              </option>
              {Array.isArray(artists) &&
                artists.map((artist) => (
                  <option key={artist.id} value={artist.artist}>
                    {artist.artist}
                  </option>
                ))}
            </select>

            <div id="artist-form-buttons">
              <button type="submit">Fetch Top Genres</button>
            </div>
          </form>
          {artistError && <div className="error-message">{artistError}</div>}

          {/* Display top genres */}
          {topGenres.length > 0 ? (
            <ul>
              {topGenres.map((genre, index) => (
                <li key={index}>
                  {genre.genre_name} ({genre.track_count} tracks)
                </li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">No genres found for this artist.</div>
          )}

          {error && <div className="error-message">{error}</div>}
        </section>

        {/* Back to Main Menu Button */}
        <div id="back-menu-wrapper">
          <button id="back-menu-button" onClick={handleBackToMenu}>
            Back to Main Menu
          </button>
        </div>
      </div>
    </div>
  );
}
