import React, { useState } from "react";
// import NavBar from "../components/navbar"; 
import "../style/SearchPage.css";
import "../index.css";

export default function SearchSong() {
  const [term, setTerm] = useState("");
  const [songs, setSongs] = useState([]);
  const [numResults, setNumResults] = useState(0);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSongs([]);
    setNumResults(0);

    if (!term) {
      setError("Please enter a genre name.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/tracks/genre/${term}`);
      if (!response.ok) {
        throw new Error("Failed to fetch data. Please check the genre name.");
      }

      const data = await response.json();
      setSongs(data);
      setNumResults(data.length);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  return (
    <div>
      <div className = 'navbar'>
        <ul>
          <li><a href='http://localhost:3000/'>HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div> 

      <div className="container text-center">
        {/* Search Form */}
        <form id="search-form" className="d-flex justify-content-center mb-4" onSubmit={handleSubmit}>
          <input
            className="form-control me-2"
            type="text"
            id="search-term"
            placeholder="Enter genre (e.g., Deep House)"
            value={term}
            onChange={(e) => setTerm(e.target.value)}
          />
          <button type="submit" className="btn btn-outline-success">
            Search
          </button>
        </form>

        {/* Error Message */}
        {error && <small id="submit-error" className="text-danger">{error}</small>}

        {/* Results Message */}
        <div id="message" className="mb-4">
          {numResults > 0 && (
            <p>
              Showing <span id="num-results">{numResults}</span> result(s).
            </p>
          )}
        </div>

        {/* Search Results */}
        <div id="content" className="row justify-content-center">
          {songs.map((song) => (
            <div key={song.track_id} className="col-4">
              <div className="card mb-4">
                <div className="card-body">
                  <h5 className="card-title">{song.title}</h5>
                  <p className="card-text">Release Date: {song.release_date}</p>
                  <a href={song.track_url} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
                    Listen
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
