import React, { useState } from "react";
import "../style/SearchPage.css"; // Import the external CSS file

export default function SearchSong() {
  const [term, setTerm] = useState("");
  const [genre, setGenre] = useState("Fiction"); // Default selected genre
  const [songs, setSongs] = useState([]);
  const [numResults, setNumResults] = useState(0);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSongs([]);
    setNumResults(0);

    if (!genre) {
      setError("Please select a genre.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/tracks/genre/${genre}`);
      if (!response.ok) {
        throw new Error("Failed to fetch songs");
      }
      const data = await response.json();
      setSongs(data);
      setNumResults(data.length);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  return (
    <div id="page-wrapper">
      {/* Search Form */}
      <form id="search-form" onSubmit={handleSubmit}>
        <label htmlFor="genre-select">Search:</label>

        {/* Dropdown for Genre Selection */}
        <select
          id="genre-select"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        >
          <option value="Fiction">Fiction</option>
          <option value="Business & Economics">Business & Economics</option>
          <option value="Romance">Romance</option>
          <option value="Thrillers">Thrillers</option>
          <option value="Historical">Historical</option>
          <option value="Literary">Literary</option>
          <option value="Suspense">Suspense</option>
        </select>

        {/* Input for Results Limit */}
        <input
          type="number"
          placeholder="5 results"
          value={term}
          onChange={(e) => setTerm(e.target.value)}
        />

        {/* Search Button */}
        <button type="submit">Search</button>
      </form>

      {/* Error Message */}
      {error && <p className="error">{error}</p>}

      {/* Results Message */}
      {numResults > 0 && (
        <p>
          Found <span id="num-results">{numResults}</span> result(s).
        </p>
      )}

      {/* Search Results */}
      <div id="search-results">
        {songs.map((song) => (
          <div key={song.track_id} className="song-item">
            <h3>{song.title}</h3>
            <p>Release Date: {song.release_date}</p>
            <a href={song.track_url} target="_blank" rel="noopener noreferrer">
              Listen
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
