import React, { useState, useEffect } from "react";
import "../style/SearchPage.css";
import "../index.css";
import LazyTable from "../components/LazyTable";

export default function SearchSong() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [error, setError] = useState("");
  const [term, setTerm] = useState("");
  const [apiRoute, setApiRoute] = useState("");

  const columns = [
    { field: "track_id", headerName: "Track ID" },
    { field: "title", headerName: "Title" },
    { 
      field: "track_url", 
      headerName: "URL", 
      renderCell: (row) => (
        <a href={row.track_url} target="_blank" rel="noopener noreferrer">
          Listen
        </a>
      ),
    },
    { field: "release_date", headerName: "Release Date" },
  ];

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres");
        if (!response.ok) {
          throw new Error(`Failed to fetch genres. Status: ${response.status}`);
        }
        const data = await response.json();
        setGenres(data.rows);
      } catch (error) {
        console.error("Error fetching genres:", error);
        setError("Failed to load genres");
      }
    };

    fetchGenres();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");
    if (!selectedGenre) {
      setError("Please select a genre.");
      return;
    }
    if (!term) {
      setError("Please select a results limit.");
      return;
    }

    const encodedGenre = encodeURIComponent(selectedGenre);
    setApiRoute(`http://localhost:8080/genres/${encodedGenre}/tracks?limit=${term}`);
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div>

      {/* Page Wrapper */}
      <div id="page-wrapper">
        {/* Search Form */}
        <form id="search-form" onSubmit={handleSubmit}>
          <label htmlFor="genre-select"></label>
          {/* Dropdown for Genre Selection */}
          <select
            id="genre-select"
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            <option value="" disabled>
              Select a Genre
            </option>
            {Array.isArray(genres) &&
              genres.map((genre) => (
                <option key={genre.id} value={genre.genre}>
                  {genre.genre}
                </option>
              ))}
          </select>

          {/* Dropdown for Results Limit */}
          <select
            id="results-limit"
            value={term}
            onChange={(e) => setTerm(e.target.value)}
          >
            <option value="" disabled>
              Select results limit
            </option>
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="50">50</option>
            <option value="100">100</option>
          </select>

          {/* Search Button */}
          <button type="submit">Search</button>
        </form>

        {/* Display Error Message */}
        <div></div>
        {error && <div className="error-message">{error}</div>}

        {/* Search Results */}
        {apiRoute && (
          <LazyTable
            route={apiRoute}
            columns={columns}
            defaultPageSize={10}
            rowsPerPageOptions={[5, 10, 25]}
          />
        )}
      </div>
    </div>
  );
}
