import React, { useState, useEffect } from 'react';
import '../style/SoundPage.css';

export default function MusicInsights() {
  const [danceabilityRows, setDanceabilityRows] = useState([]);
  const [loudnessRows, setLoudnessRows] = useState([]);
  const [filteredLoudnessRows, setFilteredLoudnessRows] = useState([]);
  const [genres, setGenres] = useState([]); // List of genres for the dropdown
  const [selectedGenre, setSelectedGenre] = useState("All"); // Selected genre
  const [topN, setTopN] = useState(5); // Number of top songs to display (default: 5)
  const [audioProfilesRows, setAudioProfilesRows] = useState([]);
  const [moodRows, setMoodRows] = useState([]);
  const [filteredMoodRows, setFilteredMoodRows] = useState([]);
  const [error, setError] = useState("");
  const [selectedMood, setSelectedMood] = useState("All");
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [currentPage, setCurrentPage] = useState(1);

  // Fetch data for all tables
  useEffect(() => {
    fetchDanceabilityData();
    fetchLoudnessData();
    fetchAudioProfilesData();
    fetchSongsByMood();
  }, []);

  // Fetch danceability data
  const fetchDanceabilityData = async () => {
    setError(""); // Reset error before fetching
    try {
      const response = await fetch("http://localhost:8080/tracks/above-average-danceability");
      if (!response.ok) throw new Error(`Failed to fetch danceability data. Status: ${response.status}`);
      const data = await response.json();

      // Randomly select 5 rows
      const shuffledRows = data.rows.sort(() => 0.5 - Math.random());
      const randomRows = shuffledRows.slice(0, 5);
      setDanceabilityRows(randomRows);
    } catch (err) {
      console.error(err);
      setError("Failed to load danceability data.");
    }
  };

  // Fetch loudness data and genres
  const fetchLoudnessData = async () => {
    try {
      const response = await fetch("http://localhost:8080/tracks/rank-by-loudness");
      if (!response.ok) throw new Error(`Failed to fetch loudness data. Status: ${response.status}`);
      const data = await response.json();

      setLoudnessRows(data.rows || []);
      setFilteredLoudnessRows(data.rows || []);

      // Extract unique genres for the dropdown
      const uniqueGenres = Array.from(new Set(data.rows.map((row) => row.genre_name)));
      setGenres(["All", ...uniqueGenres]);
    } catch (err) {
      console.error(err);
      setError("Failed to load loudness data.");
    }
  };

  // Fetch audio profiles data
  const fetchAudioProfilesData = async () => {
    try {
      const response = await fetch("http://localhost:8080/audio-feature-profiles");
      if (!response.ok) throw new Error(`Failed to fetch audio profiles data. Status: ${response.status}`);
      const data = await response.json();
      setAudioProfilesRows(data.rows || []);
    } catch (err) {
      console.error(err);
      setError("Failed to load audio profiles data.");
    }
  };

  // Fetch songs by mood
  const fetchSongsByMood = async () => {
    try {
      const response = await fetch("http://localhost:8080/songs/mood");
      if (!response.ok) throw new Error(`Failed to fetch songs by mood. Status: ${response.status}`);
      const data = await response.json();
      setMoodRows(data || []);
      setFilteredMoodRows(data || []); // Initially show all moods
    } catch (err) {
      console.error(err);
      setError("Failed to load songs by mood.");
    }
  };

  // Handle genre selection
  const handleGenreChange = (event) => {
    const genre = event.target.value;
    setSelectedGenre(genre);
    filterLoudnessRows(genre, topN);
  };

  // Handle top-N selection
  const handleTopNChange = (event) => {
    const n = parseInt(event.target.value, 10);
    setTopN(n);
    filterLoudnessRows(selectedGenre, n);
  };

  // Filter loudness rows based on genre and top-N
  const filterLoudnessRows = (genre, n) => {
    let filtered = loudnessRows;

    if (genre !== "All") {
      filtered = loudnessRows.filter((row) => row.genre_name === genre);
    }

    // Sort by loudness descending and limit to top N
    filtered = filtered.sort((a, b) => b.loudness - a.loudness).slice(0, n);

    setFilteredLoudnessRows(filtered);
  };

  return (
    <>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

      <div id="sound-page-wrapper">
        <h1>Track Explorer</h1>

        {/* Main Page Wrapper */}
        <div id="page-wrapper">
          <div id="content-wrapper">
            {/* Left Panel */}
            <div className="panel">
              {/* Danceability Table */}
              <section className="table-section">
                <h2>Wanna Dance?</h2>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <table className="data-table">
                      <thead>
                        <tr>
                          <th>Track Title</th>
                          <th>Genre</th>
                          <th>Danceability</th>
                        </tr>
                      </thead>
                      <tbody>
                        {danceabilityRows.length > 0 ? (
                          danceabilityRows.map((row, index) => (
                            <tr key={index}>
                              <td>{row.title}</td>
                              <td>{row.genre_name}</td>
                              <td>{row.danceability}</td>
                            </tr>
                          ))
                        ) : (
                          <tr>
                            <td colSpan="3">No data available.</td>
                          </tr>
                        )}
                      </tbody>
                    </table>
                    <div id="refresh-button-container">
                      <button id="refresh-button" onClick={fetchDanceabilityData}>
                        Refresh Songs
                      </button>
                    </div>
                  </>
                )}
              </section>

              {/* Loudness Table */}
              <section className="table-section">
                <h2>Rank Tracks by Loudness</h2>
                <div className="controls">
                  <label htmlFor="genreFilter">Select Genre: </label>
                  <select id="genreFilter" value={selectedGenre} onChange={handleGenreChange}>
                    {genres.map((genre, index) => (
                      <option key={index} value={genre}>
                        {genre}
                      </option>
                    ))}
                  </select>

                  <label htmlFor="topN">Top Songs: </label>
                  <select id="topN" value={topN} onChange={handleTopNChange}>
                    <option value={1}>1</option>
                    <option value={3}>3</option>
                    <option value={5}>5</option>
                  </select>
                </div>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Rank</th>
                        <th>Track Title</th>
                        <th>Genre</th>
                        <th>Loudness (dB)</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredLoudnessRows.length > 0 ? (
                        filteredLoudnessRows.map((row, index) => (
                          <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{row.title}</td>
                            <td>{row.genre_name}</td>
                            <td>{row.loudness}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="4">No data available.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </section>
            </div>

            {/* Right Panel */}
            <div className="panel">
              {/* Audio Profiles Table */}
              <section className="table-section">
                <h2>Top Audio Profiles by Genre</h2>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Genre</th>
                        <th>Valence</th>
                        <th>Energy</th>
                        <th>Danceability</th>
                        <th>Profile Count</th>
                      </tr>
                    </thead>
                    <tbody>
                      {audioProfilesRows.length > 0 ? (
                        audioProfilesRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.genre_name}</td>
                            <td>{row.valence}</td>
                            <td>{row.energy}</td>
                            <td>{row.danceability}</td>
                            <td>{row.profile_count}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="5">No data available.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </section>

              {/* Songs by Mood Table */}
              <section className="table-section">
                <h2>Songs by Mood</h2>
                <div className="controls">
                  <label htmlFor="moodFilter">Filter by Mood: </label>
                  <select id="moodFilter" value={selectedMood} onChange={handleMoodChange}>
                    <option value="All">All</option>
                    <option value="Upbeat">Upbeat</option>
                    <option value="Melancholic">Melancholic</option>
                    <option value="Energetic">Energetic</option>
                    <option value="Calm">Calm</option>
                  </select>
                  <div className="rows-per-page">
                    <label htmlFor="rowsPerPage">Rows per page:</label>
                    <select id="rowsPerPage" value={rowsPerPage} onChange={handleRowsPerPageChange}>
                      <option value={5}>5</option>
                      <option value={10}>10</option>
                      <option value={25}>25</option>
                    </select>
                    <span className="rows-label">Rows</span>
                  </div>
                </div>
                {filteredMoodRows.length === 0 && !error ? (
                  <div className="info-message">No songs available for the selected mood.</div>
                ) : error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <table className="data-table">
                      <thead>
                        <tr>
                          <th>Track Title</th>
                          <th>Valence</th>
                          <th>Energy</th>
                          <th>Mood</th>
                        </tr>
                      </thead>
                      <tbody>
                        {paginatedRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.title || "Unknown Title"}</td>
                            <td>{row.valence !== undefined ? row.valence.toFixed(3) : "N/A"}</td>
                            <td>{row.energy !== undefined ? row.energy.toFixed(3) : "N/A"}</td>
                            <td>{row.mood || "N/A"}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    <div className="pagination-controls">
                      <button disabled={currentPage === 1} onClick={handlePreviousPage}>
                        Previous
                      </button>
                      <span>
                        Page {currentPage} of {totalPages}
                      </span>
                      <button disabled={currentPage === totalPages} onClick={handleNextPage}>
                        Next
                      </button>
                    </div>
                  </>
                )}
              </section>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
