import React, { useState, useEffect } from "react";

export default function FeatureSelection() {
  const [songs, setSongs] = useState([]);
  const [featureFilters, setFeatureFilters] = useState({
    danceability: 0.5,
    loudness: -30,
    tempo: 120,
    energy: 0.5,
  });

  const [error, setError] = useState("");

  // Fetch all songs on component mount for initial display
  useEffect(() => {
    const fetchAllSongs = async () => {
      try {
        const response = await fetch("http://localhost:8080/getAllAudioFeatures");
        if (!response.ok) {
          throw new Error(`Failed to fetch songs. Status: ${response.status}`);
        }
        const data = await response.json();
        setSongs(data || []); // Assuming data is an array of songs
      } catch (error) {
        console.error("Error fetching songs:", error);
        setError("Failed to load songs.");
      }
    };

    fetchAllSongs();
  }, []);

  // Handle single slider value change
  const handleFeatureChange = (feature, value) => {
    setFeatureFilters((prev) => ({
      ...prev,
      [feature]: value,
    }));
  };

  // Handle feature-based search
  const handleFeatureSearch = async (e) => {
    e.preventDefault();
    setError(""); // Reset error state
    setSongs([]); // Reset song list

    try {
      const queryParams = new URLSearchParams({
        danceability: featureFilters.danceability,
        loudness: featureFilters.loudness,
        tempo: featureFilters.tempo,
        energy: featureFilters.energy,
      });

      const response = await fetch(
        `http://localhost:8080/getTopAudioFeatureProfiles?${queryParams.toString()}`
      );

      if (!response.ok) {
        throw new Error(`Failed to fetch songs by features. Status: ${response.status}`);
      }

      const data = await response.json();
      setSongs(data || []); // Assuming data is an array of songs
    } catch (error) {
      console.error("Error fetching songs by features:", error);
      setError("Failed to load songs by features.");
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/feature-selection">Feature Selection</a></li>
        </ul>
      </div>

      {/* Feature Selection Section */}
      <div style={{ margin: "20px" }}>
        <h2>Select Songs by Features</h2>
        <form onSubmit={handleFeatureSearch}>
          {/* Grid container */}
          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
            {/* Danceability */}
            <div style={{ textAlign: "left" }}>
              <label style={{ fontWeight: "bold" }}>Danceability</label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={featureFilters.danceability}
                onChange={(e) =>
                  handleFeatureChange("danceability", parseFloat(e.target.value))
                }
                style={{ width: "100%", marginTop: "5px" }}
              />
              <p style={{ marginTop: "5px", color: "#555" }}>
                {featureFilters.danceability.toFixed(2)}
              </p>
            </div>

            {/* Loudness */}
            <div style={{ textAlign: "left" }}>
              <label style={{ fontWeight: "bold" }}>Loudness</label>
              <input
                type="range"
                min="-60"
                max="0"
                step="1"
                value={featureFilters.loudness}
                onChange={(e) =>
                  handleFeatureChange("loudness", parseFloat(e.target.value))
                }
                style={{ width: "100%", marginTop: "5px" }}
              />
              <p style={{ marginTop: "5px", color: "#555" }}>
                {featureFilters.loudness} dB
              </p>
            </div>

            {/* Tempo */}
            <div style={{ textAlign: "left" }}>
              <label style={{ fontWeight: "bold" }}>Tempo</label>
              <input
                type="range"
                min="0"
                max="250"
                step="1"
                value={featureFilters.tempo}
                onChange={(e) =>
                  handleFeatureChange("tempo", parseFloat(e.target.value))
                }
                style={{ width: "100%", marginTop: "5px" }}
              />
              <p style={{ marginTop: "5px", color: "#555" }}>
                {featureFilters.tempo} BPM
              </p>
            </div>

            {/* Energy */}
            <div style={{ textAlign: "left" }}>
              <label style={{ fontWeight: "bold" }}>Energy</label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={featureFilters.energy}
                onChange={(e) =>
                  handleFeatureChange("energy", parseFloat(e.target.value))
                }
                style={{ width: "100%", marginTop: "5px" }}
              />
              <p style={{ marginTop: "5px", color: "#555" }}>
                {featureFilters.energy.toFixed(2)}
              </p>
            </div>
          </div>

          <button type="submit" style={{ marginTop: "20px" }}>
            Search Songs
          </button>
        </form>
        {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      </div>

      {/* Songs Display Section */}
      <div>
        <h2>Song Results</h2>
        {songs.length > 0 ? (
          songs.map((song) => (
            <div
              key={song.id}
              style={{
                border: "1px solid #ddd",
                padding: "10px",
                marginBottom: "10px",
                borderRadius: "5px",
              }}
            >
              <h3>{song.title}</h3>
              <p>Artist: {song.artist}</p>
              <p>Album: {song.album}</p>
              <p>Danceability: {song.danceability}</p>
              <p>Loudness: {song.loudness} dB</p>
              <p>Tempo: {song.tempo} BPM</p>
              <p>Energy: {song.energy}</p>
            </div>
          ))
        ) : (
          <p>No songs found.</p>
        )}
      </div>
    </div>
  );

  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
