import React, { useState, useEffect } from "react";
import "../style/Collaboration.css";

export default function CollaborativeReleases() {
  const [collaborations, setCollaborations] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [minArtists, setMinArtists] = useState(2);

  // State for Dynamic Heading
  const [heading, setHeading] = useState("Collaborative Music Insights");

  // Fetch data from API
  useEffect(() => {
    const fetchCollaborations = async () => {
      try {
        const response = await fetch("http://localhost:8080/collaborativeReleases");
        if (!response.ok) {
          throw new Error(`Failed to fetch collaborations: ${response.status}`);
        }
        const data = await response.json();
        setCollaborations(data);
        // Update heading dynamically based on data count
        setHeading(`Collaborations Found: ${data.length}`);
      } catch (error) {
        console.error("Error fetching collaborations:", error);
        setHeading("Failed to Load Data");
      }
    };

    fetchCollaborations();
  }, []);

  // Filter and search data
  const filteredCollaborations = collaborations.filter(
    (collab) =>
      collab.artist_count >= minArtists &&
      (collab.release_title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        collab.artist_names.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  // Update heading based on user filters
  useEffect(() => {
    if (filteredCollaborations.length === 0) {
      setHeading("No Matches Found");
    } else {
      setHeading(`Collaborations Found: ${filteredCollaborations.length}`);
    }
  }, [filteredCollaborations]);

  return (
    <div id="collaborative-releases-page">
      <header>
        <h1>{heading}</h1>
        <p>Explore releases featuring multiple artists and their collaborations.</p>
      </header>

      {/* Filters Section */}
      <div id="filters">
        <input
          type="text"
          placeholder="Search by title or artist"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <input
          type="number"
          min="2"
          placeholder="Minimum Artists"
          value={minArtists}
          onChange={(e) => setMinArtists(Number(e.target.value))}
        />
      </div>

      {/* Table Section */}
      <div id="table-container">
        <table>
          <thead>
            <tr>
              <th>Release Title</th>
              <th>Number of Collaborators</th>
              <th>Artists</th>
            </tr>
          </thead>
          <tbody>
            {filteredCollaborations.map((collab) => (
              <tr key={collab.release_id}>
                <td>{collab.release_title}</td>
                <td>{collab.artist_count}</td>
                <td>{collab.artist_names}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );



  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
