import React from 'react';
import LazyTable from '../components/LazyTable'; // Assuming LazyTable is in the components folder
import '../style/SoundPage.css';

export default function MusicInsights() {
  const danceabilityColumns = [
    { field: 'title', headerName: 'Track Title' },
    { field: 'genre_name', headerName: 'Genre' },
    { field: 'danceability', headerName: 'Danceability' },
  ];

  const loudnessColumns = [
    { field: 'loudness_rank', headerName: 'Rank' },
    { field: 'title', headerName: 'Track Title' },
    { field: 'genre_name', headerName: 'Genre' },
    { field: 'loudness', headerName: 'Loudness (dB)' },
  ];

  const audioProfilesColumns = [
    { field: 'genre_name', headerName: 'Genre' },
    { field: 'valence', headerName: 'Valence' },
    { field: 'energy', headerName: 'Energy' },
    { field: 'danceability', headerName: 'Danceability' },
    { field: 'profile_count', headerName: 'Profile Count' },
  ];

  // Page and Section Styles
  const pageStyle = {
    display: 'flex',
    flexDirection: 'column', // Stack content vertically
    alignItems: 'center', // Center the heading horizontally
    height: '100vh',
    padding: '20px',
    backgroundColor: '#f8f4e3', // Light beige background
  };

  const headerStyle = {
    fontSize: '2.5rem',
    color: '#4caf50', // Green heading
    fontFamily: 'Georgia, serif',
    marginBottom: '20px',
  };

  const contentStyle = {
    display: 'flex',
    flexDirection: 'row', // Split content into left and right halves
    justifyContent: 'space-between',
    width: '100%',
  };

  const leftPanelStyle = {
    flex: 1,
    marginRight: '10px',
  };

  const rightPanelStyle = {
    flex: 1,
    marginLeft: '10px',
  };

  const tableSectionStyle = {
    marginBottom: '20px',
  };

  return (
    <div style={pageStyle}>
      {/* Main Heading */}
      <h1 style={headerStyle}>Let's Play with Some Music</h1>

      {/* Split Content */}
      <div style={contentStyle}>
        {/* Left Panel: Two LazyTables */}
        <div style={leftPanelStyle}>
          <section style={tableSectionStyle}>
            <h2>Tracks with Above-Average Danceability</h2>
            <LazyTable
              route="http://localhost:8080/aboveAverageDanceability"
              columns={danceabilityColumns}
              defaultPageSize={5}
              rowsPerPageOptions={[5, 10, 25]}
            />
          </section>
          <section>
            <h2>Rank Tracks by Loudness</h2>
            <LazyTable
              route="http://localhost:8080/rankTracksByLoudness"
              columns={loudnessColumns}
              defaultPageSize={5}
              rowsPerPageOptions={[5, 10, 25]}
            />
          </section>
        </div>

        {/* Right Panel: One LazyTable */}
        <div style={rightPanelStyle}>
          <section>
            <h2>Top 3 Audio Profiles by Genre</h2>
            <LazyTable
              route="http://localhost:8080/topAudioProfiles"
              columns={audioProfilesColumns}
              defaultPageSize={5}
              rowsPerPageOptions={[5, 10, 25]}
            />
          </section>
        </div>
      </div>
    </div>
  );




  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
