import React, { useState, useEffect } from "react";
import "../style/SoundPage.css";

export default function MusicInsights() {
  const [danceabilityRows, setDanceabilityRows] = useState([]);
  const [energyRows, setEnergyRows] = useState([]);
  const [filteredEnergyRows, setFilteredEnergyRows] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("All");
  const [topN, setTopN] = useState(5);
  const [moodRows, setMoodRows] = useState([]);
  const [filteredMoodRows, setFilteredMoodRows] = useState([]);
  const [error, setError] = useState("");
  const [selectedMood, setSelectedMood] = useState("All");
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [currentPage, setCurrentPage] = useState(1);

  const [instrumentalness, setInstrumentalness] = useState(0);
  const [energy, setEnergy] = useState(0);
  const [valence, setValence] = useState(0);
  const [liveness, setLiveness] = useState(0);
  const [acousticness, setAcousticness] = useState(0);
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchDanceabilityData();
    fetchEnergyData();
    fetchSongsByMood();
  }, []);

  const fetchDanceabilityData = async () => {
    try {
      const response = await fetch("http://localhost:8080/tracks/above-average-danceability");
      if (!response.ok) throw new Error(`Failed to fetch danceability data.`);
      const data = await response.json();
      const randomRows = data.rows.sort(() => 0.5 - Math.random()).slice(0, 5);
      setDanceabilityRows(randomRows);
    } catch (err) {
      setError("Failed to load danceability data.");
    }
  };

  const fetchEnergyData = async () => {
    try {
      const response = await fetch("http://localhost:8080/tracks/rank-by-energy");
      if (!response.ok) throw new Error(`Failed to fetch energy data.`);
      const data = await response.json();

      setEnergyRows(data.rows || []);
      setFilteredEnergyRows(data.rows.slice(0, topN));
      setGenres(["All", ...new Set(data.rows.map((row) => row.genre_name))]);
    } catch (err) {
      setError("Failed to load energy data.");
    }
  };

  const fetchSongsByMood = async () => {
    try {
      const response = await fetch("http://localhost:8080/songs/mood");
      if (!response.ok) throw new Error(`Failed to fetch songs by mood.`);
      const data = await response.json();
      setMoodRows(data || []);
      setFilteredMoodRows(data || []);
    } catch (err) {
      setError("Failed to load songs by mood.");
    }
  };

  const fetchSongs = async () => {
    try {
      setLoading(true);
      setError("");

      const queryParams = new URLSearchParams({
        instrumentalness: instrumentalness > 0 ? instrumentalness : undefined,
        energy: energy > 0 ? energy : undefined,
        valence: valence > 0 ? valence : undefined,
        liveness: liveness > 0 ? liveness : undefined,
        acousticness: acousticness > 0 ? acousticness : undefined,
      });

      const response = await fetch(
        `http://localhost:8080/songs-by-audio-features?${queryParams}`
      );

      if (!response.ok) throw new Error(`Failed to fetch songs.`);
      const data = await response.json();
      setSongs(data.rows || []);
    } catch (err) {
      setError("Failed to fetch songs. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const clearFilters = () => {
    setInstrumentalness(0);
    setEnergy(0);
    setValence(0);
    setLiveness(0);
    setAcousticness(0);
    setSongs([]);
    setError("");
  };

  return (
    <div id="sound-page-wrapper">
      <div id="navbar">
        <ul>
          <li><a href="/">HomePage</a></li>
          <li><a href="/search">Search Page</a></li>
          <li><a href="/genre">Genre Page</a></li>
        </ul>
      </div>
      <h1>Track Explorer</h1>

      {/* Mood Table */}
      <section className="table-section">
        <h2>How Are You Feeling?</h2>
        <div>
          <label htmlFor="moodFilter">Filter by Mood: </label>
          <select
            id="moodFilter"
            value={selectedMood}
            onChange={(e) => setSelectedMood(e.target.value)}
          >
            <option value="All">All</option>
            <option value="Upbeat">Upbeat</option>
          </select>
        </div>
        <table>
          <thead>
            <tr>
              <th>Track Title</th>
              <th>Valence</th>
              <th>Energy</th>
              <th>Mood</th>
            </tr>
          </thead>
          <tbody>
            {filteredMoodRows.map((row, index) => (
              <tr key={index}>
                <td>{row.title}</td>
                <td>{row.valence?.toFixed(2)}</td>
                <td>{row.energy?.toFixed(2)}</td>
                <td>{row.mood}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Filter Songs by Audio Features */}
      <div>
        <header>
          <h1>Filter Songs by Audio Features</h1>
          <p>Use the sliders below to filter songs based on their audio feature values.</p>
        </header>

        <div className="slider-container" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
          <div><label>Instrumentalness: {instrumentalness}</label>
            <input type="range" min="0" max="1" step="0.01" value={instrumentalness}
              onChange={(e) => setInstrumentalness(parseFloat(e.target.value))} /></div>
          <div><label>Energy: {energy}</label>
            <input type="range" min="0" max="1" step="0.01" value={energy}
              onChange={(e) => setEnergy(parseFloat(e.target.value))} /></div>
          <div><label>Valence: {valence}</label>
            <input type="range" min="0" max="1" step="0.01" value={valence}
              onChange={(e) => setValence(parseFloat(e.target.value))} /></div>
        </div>
        <button onClick={fetchSongs} disabled={loading}>
          {loading ? "Loading..." : "Search Songs"}
        </button>
        <button onClick={clearFilters}>Clear Filters</button>

        {/* Results */}
        {error && <p>{error}</p>}
        {songs.length > 0 && (
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Instrumentalness</th>
                <th>Energy</th>
                <th>Valence</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song, index) => (
                <tr key={index}>
                  <td>{song.title}</td>
                  <td>{song.instrumentalness}</td>
                  <td>{song.energy}</td>
                  <td>{song.valence}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
