import React, { useState, useEffect } from 'react';
import '../style/SoundPage.css';

export default function MusicInsights() {
  const [danceabilityRows, setDanceabilityRows] = useState([]);
  const [loudnessRows, setLoudnessRows] = useState([]);
  const [audioProfilesRows, setAudioProfilesRows] = useState([]);
  const [moodRows, setMoodRows] = useState([]);
  const [filteredMoodRows, setFilteredMoodRows] = useState([]);
  const [error, setError] = useState("");
  const [selectedMood, setSelectedMood] = useState("All");
  const [rowsPerPage, setRowsPerPage] = useState(5); // Default: Show 5 rows
  const [currentPage, setCurrentPage] = useState(1);

  // Fetch data for each table on mount
  useEffect(() => {
    const fetchDanceabilityData = async () => {
      try {
        const response = await fetch("http://localhost:8080/tracks/above-average-danceability");
        if (!response.ok) throw new Error(`Failed to fetch danceability data. Status: ${response.status}`);
        const data = await response.json();
        setDanceabilityRows(data.rows || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load danceability data.");
      }
    };

    const fetchLoudnessData = async () => {
      try {
        const response = await fetch("http://localhost:8080/tracks/rank-by-loudness");
        if (!response.ok) throw new Error(`Failed to fetch loudness data. Status: ${response.status}`);
        const data = await response.json();
        setLoudnessRows(data.rows || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load loudness data.");
      }
    };

    const fetchAudioProfilesData = async () => {
      try {
        const response = await fetch("http://localhost:8080/audio-feature-profiles");
        if (!response.ok) throw new Error(`Failed to fetch audio profiles data. Status: ${response.status}`);
        const data = await response.json();
        setAudioProfilesRows(data.rows || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load audio profiles data.");
      }
    };

    const fetchSongsByMood = async () => {
      try {
        const response = await fetch("http://localhost:8080/songs/mood");
        if (!response.ok) throw new Error(`Failed to fetch songs by mood. Status: ${response.status}`);
        const data = await response.json();
        console.log("Fetched Mood Data:", data);
        setMoodRows(data || []);
        setFilteredMoodRows(data || []); // Initially show all moods
      } catch (err) {
        console.error(err);
        setError("Failed to load songs by mood.");
      }
    };

    fetchDanceabilityData();
    fetchLoudnessData();
    fetchAudioProfilesData();
    fetchSongsByMood();
  }, []);

  // Handle Mood Selection
  const handleMoodChange = (event) => {
    const mood = event.target.value;
    setSelectedMood(mood);
    setCurrentPage(1); // Reset to the first page

    if (mood === "All") {
      setFilteredMoodRows(moodRows);
    } else {
      const filtered = moodRows.filter((row) => row.mood === mood);
      setFilteredMoodRows(filtered);
    }
  };

  // Handle Rows Per Page Selection
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10)); // Convert string to number
    setCurrentPage(1); // Reset to the first page
  };

  // Get Paginated Rows
  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const paginatedRows = filteredMoodRows.slice(indexOfFirstRow, indexOfLastRow);

  // Handle Pagination
  const totalPages = Math.ceil(filteredMoodRows.length / rowsPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

      <div id="sound-page-wrapper">
        <h1>Track Explorer</h1>

        {/* Main Page Wrapper */}
        <div id="page-wrapper">
          <div id="content-wrapper">
            {/* Left Panel */}
            <div className="panel">
              {/* Danceability Table */}
              <section className="table-section">
                <h2>Tracks with Above-Average Danceability</h2>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Track Title</th>
                        <th>Genre</th>
                        <th>Danceability</th>
                      </tr>
                    </thead>
                    <tbody>
                      {danceabilityRows.length > 0 ? (
                        danceabilityRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.title}</td>
                            <td>{row.genre_name}</td>
                            <td>{row.danceability}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="3">No data available.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </section>

              {/* Loudness Table */}
              <section className="table-section">
                <h2>Rank Tracks by Loudness</h2>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Rank</th>
                        <th>Track Title</th>
                        <th>Genre</th>
                        <th>Loudness (dB)</th>
                      </tr>
                    </thead>
                    <tbody>
                      {loudnessRows.length > 0 ? (
                        loudnessRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.loudness_rank}</td>
                            <td>{row.title}</td>
                            <td>{row.genre_name}</td>
                            <td>{row.loudness}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="4">No data available.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </section>
            </div>

            {/* Right Panel */}
            <div className="panel">
              {/* Audio Profiles Table */}
              <section className="table-section">
                <h2>Top Audio Profiles by Genre</h2>
                {error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Genre</th>
                        <th>Valence</th>
                        <th>Energy</th>
                        <th>Danceability</th>
                        <th>Profile Count</th>
                      </tr>
                    </thead>
                    <tbody>
                      {audioProfilesRows.length > 0 ? (
                        audioProfilesRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.genre_name}</td>
                            <td>{row.valence}</td>
                            <td>{row.energy}</td>
                            <td>{row.danceability}</td>
                            <td>{row.profile_count}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="5">No data available.</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                )}
              </section>

              {/* Songs by Mood Table */}
              <section className="table-section">
                <h2>Songs by Mood</h2>
                <div className="controls">
                  <label htmlFor="moodFilter">Filter by Mood: </label>
                  <select id="moodFilter" value={selectedMood} onChange={handleMoodChange}>
                    <option value="All">All</option>
                    <option value="Happy">Happy</option>
                    <option value="Sad">Sad</option>
                    <option value="Energetic">Energetic</option>
                    <option value="Calm">Calm</option>
                  </select>
                  <label htmlFor="rowsPerPage">Rows per page: </label>
                  <select id="rowsPerPage" value={rowsPerPage} onChange={handleRowsPerPageChange}>
                    <option value={5}>5</option>
                    <option value={10}>10</option>
                    <option value={25}>25</option>
                  </select>
                </div>
                {filteredMoodRows.length === 0 && !error ? (
                  <div className="info-message">No songs available for the selected mood.</div>
                ) : error ? (
                  <div className="error-message">{error}</div>
                ) : (
                  <>
                    <table className="data-table">
                      <thead>
                        <tr>
                          <th>Track Title</th>
                          <th>Valence</th>
                          <th>Energy</th>
                          <th>Mood</th>
                        </tr>
                      </thead>
                      <tbody>
                        {paginatedRows.map((row, index) => (
                          <tr key={index}>
                            <td>{row.title || "Unknown Title"}</td>
                            <td>{row.valence !== undefined ? row.valence.toFixed(3) : "N/A"}</td>
                            <td>{row.energy !== undefined ? row.energy.toFixed(3) : "N/A"}</td>
                            <td>{row.mood || "N/A"}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    <div className="pagination-controls">
                      <button disabled={currentPage === 1} onClick={handlePreviousPage}>
                        Previous
                      </button>
                      <span>
                        Page {currentPage} of {totalPages}
                      </span>
                      <button disabled={currentPage === totalPages} onClick={handleNextPage}>
                        Next
                      </button>
                    </div>
                  </>
                )}
              </section>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
