    danceability: [0, 1],
    loudness: [-60, 0],
    tempo: [0, 250],
    energy: [0, 1],
  });

  const [error, setError] = useState("");

  // Fetch all songs on component mount for initial display
  useEffect(() => {
    const fetchAllSongs = async () => {
      try {
        const response = await fetch("http://localhost:8080/getAllAudioFeatures");
        if (!response.ok) {
          throw new Error(`Failed to fetch songs. Status: ${response.status}`);
        }
        const data = await response.json();
        setSongs(data); // Assuming data is an array of songs
      } catch (error) {
        console.error("Error fetching songs:", error);
        setError("Failed to load songs.");
      }
    };

    fetchAllSongs();
  }, []);

  // Handle slider changes for feature filters
  const handleFeatureChange = (feature, value) => {
    setFeatureFilters((prev) => ({
      ...prev,
      [feature]: value,
    }));
  };

  // Handle feature-based search
  const handleFeatureSearch = async (e) => {
    e.preventDefault();
    setError(""); // Reset error state
    setSongs([]); // Reset song list

    try {
      const queryParams = new URLSearchParams();
      Object.keys(featureFilters).forEach((feature) => {
        queryParams.append(`${feature}_low`, featureFilters[feature][0]);
        queryParams.append(`${feature}_high`, featureFilters[feature][1]);
      });

      const response = await fetch(
        `http://localhost:8080/getTopAudioFeatureProfiles?${queryParams.toString()}`
      );

      if (!response.ok) {
        throw new Error(`Failed to fetch songs by features. Status: ${response.status}`);
      }
      const data = await response.json();
      setSongs(data || []); // Assuming data is an array of songs
    } catch (error) {
      console.error("Error fetching songs by features:", error);
      setError("Failed to load songs by features.");
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/feature-selection">Feature Selection</a></li>
        </ul>
      </div>

      {/* Feature Selection Section */}
      <div className="feature-selection-section">
        <h2>Select Songs by Features</h2>
        <form onSubmit={handleFeatureSearch}>
          {/* Danceability */}
          <div className="feature-slider">
            <label>Danceability</label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={featureFilters.danceability[0]}
              onChange={(e) =>
                handleFeatureChange("danceability", [
                  parseFloat(e.target.value),
                  featureFilters.danceability[1],
                ])
              }
            />
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={featureFilters.danceability[1]}
              onChange={(e) =>
                handleFeatureChange("danceability", [
                  featureFilters.danceability[0],
                  parseFloat(e.target.value),
                ])
              }
            />
            <p>
              {featureFilters.danceability[0]} - {featureFilters.danceability[1]}
            </p>
          </div>

          {/* Loudness */}
          <div className="feature-slider">
            <label>Loudness</label>
            <input
              type="range"
              min="-60"
              max="0"
              step="1"
              value={featureFilters.loudness[0]}
              onChange={(e) =>
                handleFeatureChange("loudness", [
                  parseFloat(e.target.value),
                  featureFilters.loudness[1],
                ])
              }
            />
            <input
              type="range"
              min="-60"
              max="0"
              step="1"
              value={featureFilters.loudness[1]}
              onChange={(e) =>
                handleFeatureChange("loudness", [
                  featureFilters.loudness[0],
                  parseFloat(e.target.value),
                ])
              }
            />
            <p>
              {featureFilters.loudness[0]} dB - {featureFilters.loudness[1]} dB
            </p>
          </div>

          {/* Tempo */}
          <div className="feature-slider">
            <label>Tempo</label>
            <input
              type="range"
              min="0"
              max="250"
              step="1"
              value={featureFilters.tempo[0]}
              onChange={(e) =>
                handleFeatureChange("tempo", [
                  parseFloat(e.target.value),
                  featureFilters.tempo[1],
                ])
              }
            />
            <input
              type="range"
              min="0"
              max="250"
              step="1"
              value={featureFilters.tempo[1]}
              onChange={(e) =>
                handleFeatureChange("tempo", [
                  featureFilters.tempo[0],
                  parseFloat(e.target.value),
                ])
              }
            />
            <p>
              {featureFilters.tempo[0]} BPM - {featureFilters.tempo[1]} BPM
            </p>
          </div>

          {/* Energy */}
          <div className="feature-slider">
            <label>Energy</label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={featureFilters.energy[0]}
              onChange={(e) =>
                handleFeatureChange("energy", [
                  parseFloat(e.target.value),
                  featureFilters.energy[1],
                ])
              }
            />
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={featureFilters.energy[1]}
              onChange={(e) =>
                handleFeatureChange("energy", [
                  featureFilters.energy[0],
                  parseFloat(e.target.value),
                ])
              }
            />
            <p>
              {featureFilters.energy[0]} - {featureFilters.energy[1]}
            </p>
          </div>

          <button type="submit">Search Songs</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>

      {/* Songs Display Section */}
      <div className="songs-list">
        <h2>Song Results</h2>
        {songs.length > 0 ? (
          songs.map((song) => (
            <div key={song.id} className="song-card">
              <h3>{song.title}</h3>
              <p>Artist: {song.artist}</p>
              <p>Album: {song.album}</p>
              <p>Danceability: {song.danceability}</p>
              <p>Loudness: {song.loudness} dB</p>
              <p>Tempo: {song.tempo} BPM</p>
              <p>Energy: {song.energy}</p>
            </div>
          ))
        ) : (
          <p>No songs found.</p>
        )}
      </div>
    </div>
  );


  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
