import React, { useState, useEffect } from "react";
import "../style/SearchPage.css"; // Import the CSS file

export default function SearchSong() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [songs, setSongs] = useState([]);
  const [numResults, setNumResults] = useState(0);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres");
        if (!response.ok) {
          throw new Error("Failed to fetch genres");
        }
        const data = await response.json();
        setGenres(data);
        setSelectedGenre(data[0]?.Genre || "");
      } catch (err) {
        console.error(err);
        setError("Failed to load genres");
      }
    };

    fetchGenres();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSongs([]);
    setNumResults(0);

    if (!selectedGenre) {
      setError("Please select a genre.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/tracks/genre/${selectedGenre}`);
      if (!response.ok) {
        throw new Error("Failed to fetch songs");
      }
      const data = await response.json();
      setSongs(data);
      setNumResults(data.length);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  return (
    <div>
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div>

      <div id="page-wrapper">
        {/* Search Form */}
        <form id="search-form" onSubmit={handleSubmit}>
          <label htmlFor="genre-select">Search:</label>
          <select
            id="genre-select"
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            {genres.map((genre) => (
              <option key={genre.ID} value={genre.Genre}>
                {genre.Genre}
              </option>
            ))}
          </select>
          <button type="submit">Search</button>
        </form>

        {error && <p className="error">{error}</p>}
        {numResults > 0 && <p>Found {numResults} result(s).</p>}

        <div id="search-results">
          {songs.map((song) => (
            <div key={song.track_id}>
              <h3>{song.title}</h3>
              <p>Release Date: {song.release_date}</p>
              <a href={song.track_url} target="_blank" rel="noopener noreferrer">Listen</a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
