import React, { useState, useEffect } from 'react';
import '../style/SoundPage.css';

export default function MusicInsights() {
  const [danceabilityRows, setDanceabilityRows] = useState([]);
  const [energyRows, setEnergyRows] = useState([]);
  const [filteredEnergyRows, setFilteredEnergyRows] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("All");
  const [topN, setTopN] = useState(5);
  const [audioProfilesRows, setAudioProfilesRows] = useState([]);
  const [moodRows, setMoodRows] = useState([]);
  const [filteredMoodRows, setFilteredMoodRows] = useState([]);
  const [error, setError] = useState("");
  const [selectedMood, setSelectedMood] = useState("All");
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [currentPage, setCurrentPage] = useState(1);
  const [audioFeatureResults, setAudioFeatureResults] = useState([]); // New state for audio feature results
  const [featureInputs, setFeatureInputs] = useState({
    instrumentalness: "",
    energy: "",
    valence: "",
    liveness: "",
    acousticness: "",
  });

  useEffect(() => {
    fetchDanceabilityData();
    fetchEnergyData();
    fetchAudioProfilesData();
    fetchSongsByMood();
  }, []);

  // Fetch danceability data
  const fetchDanceabilityData = async () => {
    try {
      const response = await fetch("http://localhost:8080/tracks/above-average-danceability");
      if (!response.ok) throw new Error(`Failed to fetch danceability data. Status: ${response.status}`);
      const data = await response.json();

      const randomRows = data.rows.sort(() => 0.5 - Math.random()).slice(0, 5);
      setDanceabilityRows(randomRows);
    } catch (err) {
      console.error(err);
      setError("Failed to load danceability data.");
    }
  };

  // Fetch energy data and genres
  const fetchEnergyData = async () => {
    try {
      const response = await fetch("http://localhost:8080/tracks/rank-by-energy");
      if (!response.ok) throw new Error(`Failed to fetch energy data. Status: ${response.status}`);
      const data = await response.json();

      setEnergyRows(data.rows || []);
      setFilteredEnergyRows(applyFilters(data.rows || [], selectedGenre, topN));

      const uniqueGenres = Array.from(new Set(data.rows.map((row) => row.genre_name)));
      setGenres(["All", ...uniqueGenres]);
    } catch (err) {
      console.error(err);
      setError("Failed to load energy data.");
    }
  };

  // Fetch audio profiles data
  const fetchAudioProfilesData = async () => {
    try {
      const response = await fetch("http://localhost:8080/audio-feature-profiles");
      if (!response.ok) throw new Error(`Failed to fetch audio profiles data. Status: ${response.status}`);
      const data = await response.json();
      setAudioProfilesRows(data.rows || []);
    } catch (err) {
      console.error(err);
      setError("Failed to load audio profiles data.");
    }
  };

  // Fetch songs by mood
  const fetchSongsByMood = async () => {
    try {
      const response = await fetch("http://localhost:8080/songs/mood");
      if (!response.ok) throw new Error(`Failed to fetch songs by mood. Status: ${response.status}`);
      const data = await response.json();
      setMoodRows(data || []);
      setFilteredMoodRows(data || []);
    } catch (err) {
      console.error(err);
      setError("Failed to load songs by mood.");
    }
  };

  // Fetch songs by audio features
  const fetchSongsByAudioFeatures = async () => {
    setError("");
    setAudioFeatureResults([]);
    try {
      const queryParams = new URLSearchParams(featureInputs).toString();
      const response = await fetch(`http://localhost:8080/songs/audio-features?${queryParams}`);
      if (!response.ok) throw new Error(`Failed to fetch songs by audio features. Status: ${response.status}`);
      const data = await response.json();
      setAudioFeatureResults(data || []);
    } catch (err) {
      console.error(err);
      setError("Failed to fetch songs by audio features.");
    }
  };

  // Handle changes to audio feature inputs
  const handleFeatureInputChange = (e) => {
    const { name, value } = e.target;
    setFeatureInputs((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <>
      {/* Navigation Bar */}
      <div id="sound-page-wrapper">
        <div id="navbar">
          <ul>
            <li><a href="http://localhost:3000/">HomePage</a></li>
            <li><a href="http://localhost:3000/search">Search Page</a></li>
            <li><a href="http://localhost:3000/genre">Genre Page</a></li>
            <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
          </ul>
        </div>

        <h1>Track Explorer</h1>

        {/* Main Page Wrapper */}
        <div id="page-wrapper">
          <div id="content-wrapper">
            {/* New Section: Search by Audio Features */}
            <section className="table-section">
              <h2>Find Songs by Audio Features</h2>
              <div className="controls">
                <label>
                  Instrumentalness:
                  <input
                    type="number"
                    name="instrumentalness"
                    step="0.01"
                    min="0"
                    max="1"
                    value={featureInputs.instrumentalness}
                    onChange={handleFeatureInputChange}
                  />
                </label>
                <label>
                  Energy:
                  <input
                    type="number"
                    name="energy"
                    step="0.01"
                    min="0"
                    max="1"
                    value={featureInputs.energy}
                    onChange={handleFeatureInputChange}
                  />
                </label>
                <label>
                  Valence:
                  <input
                    type="number"
                    name="valence"
                    step="0.01"
                    min="0"
                    max="1"
                    value={featureInputs.valence}
                    onChange={handleFeatureInputChange}
                  />
                </label>
                <label>
                  Liveness:
                  <input
                    type="number"
                    name="liveness"
                    step="0.01"
                    min="0"
                    max="1"
                    value={featureInputs.liveness}
                    onChange={handleFeatureInputChange}
                  />
                </label>
                <label>
                  Acousticness:
                  <input
                    type="number"
                    name="acousticness"
                    step="0.01"
                    min="0"
                    max="1"
                    value={featureInputs.acousticness}
                    onChange={handleFeatureInputChange}
                  />
                </label>
              </div>
              <button onClick={fetchSongsByAudioFeatures}>Search</button>
              {error && <div className="error-message">{error}</div>}
              {audioFeatureResults.length > 0 && (
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>Track Title</th>
                      <th>URL</th>
                      <th>Instrumentalness</th>
                      <th>Energy</th>
                      <th>Valence</th>
                      <th>Liveness</th>
                      <th>Acousticness</th>
                    </tr>
                  </thead>
                  <tbody>
                    {audioFeatureResults.map((row, index) => (
                      <tr key={index}>
                        <td>{row.title}</td>
                        <td>
                          <a href={row.url} target="_blank" rel="noopener noreferrer">
                            Link
                          </a>
                        </td>
                        <td>{row.instrumentalness}</td>
                        <td>{row.energy}</td>
                        <td>{row.valence}</td>
                        <td>{row.liveness}</td>
                        <td>{row.acousticness}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </section>
          </div>
        </div>
      </div>
    </>
  );
}
