import React, { useState } from "react";
import "../style/SearchPage.css";
import "../index.css"

export default function SearchPage() {
  // State hooks
  const [term, setTerm] = useState(""); // User input for genre
  const [songs, setSongs] = useState([]); // Fetched songs
  const [numResults, setNumResults] = useState(0); // Number of results
  const [error, setError] = useState(""); // Error message

  // Handles the form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous error
    setSongs([]); // Clear previous results
    setNumResults(0); // Reset result count

    if (!term) {
      setError("Please enter a genre name.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/tracks/genre/${term}`);
      if (!response.ok) {
        throw new Error("Failed to fetch data. Please check the genre name.");
      }

      const data = await response.json();
      setSongs(data); // Update songs with API response
      setNumResults(data.length); // Update result count
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  // Function to render each song row
  const createRow = (song) => (
    <div key={song.track_id} className="col-4">
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">{song.title}</h5>
          <p className="card-text">Release Date: {song.release_date}</p>
          <a href={song.track_url} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
            Listen
          </a>
        </div>
      </div>
    </div>
  );

  return (
    <div className="container text-center">
      {/* Navigation Bar */}
      <nav id="navbar">
        <ul>
          <li>
            <a href="/">
              <strong>Home Page</strong>
            </a>
            <ul>
              <li>
                <a href="#why-reading">
                  <strong>Why Reading</strong>
                </a>
              </li>
              <li>
                <a href="#books">
                  <strong>Booklist</strong>
                </a>
              </li>
              <li>
                <a href="#reading">
                  <strong>Book Currently Reading</strong>
                </a>
              </li>
              <li>
                <a href="#sidebar">
                  <strong>Books Planned</strong>
                </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="gallery.html">
              <strong>Gallery</strong>
            </a>
          </li>
        </ul>
      </nav>

      {/* Search Form */}
      <form id="search-form" className="d-flex justify-content-center mb-4" onSubmit={handleSubmit}>
        <input
          className="form-control me-2"
          type="text"
          id="search-term"
          placeholder="Enter genre (e.g., Deep House)"
          value={term}
          onChange={(e) => setTerm(e.target.value)}
        />
        <button type="submit" className="btn btn-outline-success">
          Search
        </button>
      </form>

      {/* Error Message */}
      {error && <small id="submit-error" className="text-danger">{error}</small>}

      {/* Results Message */}
      <div id="message" className="mb-4">
        {numResults > 0 && (
          <p>
            Showing <span id="num-results">{numResults}</span> result(s).
          </p>
        )}
      </div>

      {/* Search Results */}
      <div id="content" className="row justify-content-center">
        {songs.map(createRow)}
      </div>
    </div>
  );
}
