import React, { useState } from "react";

export default function FilterSongsByAudioFeatures() {
  const [instrumentalness, setInstrumentalness] = useState(0);
  const [energy, setEnergy] = useState(0);
  const [valence, setValence] = useState(0);
  const [liveness, setLiveness] = useState(0);
  const [acousticness, setAcousticness] = useState(0);
  const [songs, setSongs] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch songs based on audio features
  const fetchSongs = async () => {
    try {
      setLoading(true); // Show loading state
      setError(""); // Clear previous errors

      // Construct query parameters dynamically based on filter values
      const queryParams = new URLSearchParams({
        instrumentalness: instrumentalness > 0 ? instrumentalness : undefined,
        energy: energy > 0 ? energy : undefined,
        valence: valence > 0 ? valence : undefined,
        liveness: liveness > 0 ? liveness : undefined,
        acousticness: acousticness > 0 ? acousticness : undefined,
      });

      const response = await fetch(
        `http://localhost:8080/songs/audio-features?${queryParams.toString()}`
      );

      if (!response.ok) {
        if (response.status === 404) {
          throw new Error("No songs found matching the specified audio features.");
        }
        throw new Error("Error fetching songs.");
      }

      const data = await response.json();
      setSongs(data); // Update state with the fetched songs
    } catch (err) {
      setError(err.message); // Set error message
      setSongs([]); // Clear songs if fetch fails
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  // Reset filters and clear results
  const clearFilters = () => {
    setInstrumentalness(0);
    setEnergy(0);
    setValence(0);
    setLiveness(0);
    setAcousticness(0);
    setSongs([]);
    setError("");
  };

  return (
    <div>
      <header>
        <h1>Filter Songs by Audio Features</h1>
        <p>Use the sliders below to filter songs based on their audio feature values.</p>
      </header>

      {/* Sliders for filtering audio features */}
      <div className="slider-container" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="instrumentalness">Instrumentalness: {instrumentalness}</label>
          <input
            id="instrumentalness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={instrumentalness}
            onChange={(e) => setInstrumentalness(parseFloat(e.target.value))}
            aria-label="Instrumentalness"
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="energy">Energy: {energy}</label>
          <input
            id="energy"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={energy}
            onChange={(e) => setEnergy(parseFloat(e.target.value))}
            aria-label="Energy"
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="valence">Valence: {valence}</label>
          <input
            id="valence"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={valence}
            onChange={(e) => setValence(parseFloat(e.target.value))}
            aria-label="Valence"
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="liveness">Liveness: {liveness}</label>
          <input
            id="liveness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={liveness}
            onChange={(e) => setLiveness(parseFloat(e.target.value))}
            aria-label="Liveness"
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="acousticness">Acousticness: {acousticness}</label>
          <input
            id="acousticness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={acousticness}
            onChange={(e) => setAcousticness(parseFloat(e.target.value))}
            aria-label="Acousticness"
          />
        </div>
      </div>

      {/* Buttons for searching and clearing filters */}
      <div style={{ margin: "20px 0", textAlign: "center" }}>
        <button onClick={fetchSongs} style={{ marginRight: "10px" }}>
          {loading ? "Loading..." : "Search Songs"}
        </button>
        <button onClick={clearFilters} disabled={loading}>
          Clear Filters
        </button>
      </div>

      {/* Display search results or error messages */}
      <div className="results">
        {error ? (
          <p className="error" style={{ color: "red" }}>{error}</p>
        ) : songs.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Instrumentalness</th>
                <th>Energy</th>
                <th>Valence</th>
                <th>Liveness</th>
                <th>Acousticness</th>
                <th>URL</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song) => (
                <tr key={song.id}>
                  <td>{song.title}</td>
                  <td>{song.instrumentalness}</td>
                  <td>{song.energy}</td>
                  <td>{song.valence}</td>
                  <td>{song.liveness}</td>
                  <td>{song.acousticness}</td>
                  <td>
                    <a href={song.url} target="_blank" rel="noopener noreferrer">
                      Listen
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          !loading && <p>No songs to display. Adjust your filters and try again.</p>
        )}
      </div>
    </div>
  );
}
