import React, { useState } from "react";
import "../style/SoundPage.css";

export default function AudioFeaturesSearch() {
  const [audioFeaturesInput, setAudioFeaturesInput] = useState({
    instrumentalness: "",
    energy: "",
    valence: "",
    liveness: "",
    acousticness: "",
  });
  const [songs, setSongs] = useState([]); // To store fetched songs
  const [error, setError] = useState(""); // Error state
  const [loading, setLoading] = useState(false); // Loading state

  // Function to fetch songs based on audio features
  const fetchSongsByAudioFeatures = async () => {
    setError(""); // Reset error state
    setSongs([]); // Clear previous songs
    setLoading(true); // Set loading state

    try {
      // Construct query parameters
      const query = new URLSearchParams(
        Object.entries(audioFeaturesInput).filter(([_, value]) => value.trim() !== "")
      ).toString();

      if (!query) {
        setError("Please provide at least one audio feature value.");
        setLoading(false);
        return;
      }

      const response = await fetch(`http://localhost:8080/songs/audio-features?${query}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch songs. Status: ${response.status}`);
      }

      const data = await response.json();

      if (data.length === 0) {
        setError("No songs found matching the specified audio features.");
      } else {
        setSongs(data);
      }
    } catch (err) {
      console.error("Error fetching songs:", err);
      setError("An error occurred while fetching songs.");
    } finally {
      setLoading(false); // Stop loading
    }
  };

  // Handle input change for audio features
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAudioFeaturesInput((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div id="audio-features-page">
      <h1>Search Songs by Audio Features</h1>

      <div id="search-controls">
        {["instrumentalness", "energy", "valence", "liveness", "acousticness"].map((feature) => (
          <div key={feature} className="input-group">
            <label htmlFor={feature}>
              {feature.charAt(0).toUpperCase() + feature.slice(1)}:
            </label>
            <input
              type="number"
              id={feature}
              name={feature}
              step="0.01"
              min="0"
              max="1"
              placeholder={`Enter ${feature} (0-1)`}
              value={audioFeaturesInput[feature]}
              onChange={handleInputChange}
            />
          </div>
        ))}
        <button onClick={fetchSongsByAudioFeatures} disabled={loading}>
          {loading ? "Searching..." : "Search"}
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {songs.length > 0 && (
        <table className="songs-table">
          <thead>
            <tr>
              <th>Track Title</th>
              <th>Instrumentalness</th>
              <th>Energy</th>
              <th>Valence</th>
              <th>Liveness</th>
              <th>Acousticness</th>
            </tr>
          </thead>
          <tbody>
            {songs.map((song, index) => (
              <tr key={index}>
                <td>{song.title}</td>
                <td>{song.instrumentalness?.toFixed(3) || "N/A"}</td>
                <td>{song.energy?.toFixed(3) || "N/A"}</td>
                <td>{song.valence?.toFixed(3) || "N/A"}</td>
                <td>{song.liveness?.toFixed(3) || "N/A"}</td>
                <td>{song.acousticness?.toFixed(3) || "N/A"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
