import React, { useState, useEffect } from "react";
import "../style/SearchPage.css";
import "../index.css";
import LazyTable from "../components/LazyTable"; // Import LazyTable

export default function SearchSong() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [term, setTerm] = useState(""); // For limiting results
  const [error, setError] = useState("");
  const [route, setRoute] = useState(""); // LazyTable route

  const columns = [
    { headerName: "Track ID", field: "track_id" },
    { headerName: "Title", field: "title" },
    {
      headerName: "URL",
      field: "track_url",
      renderCell: (row) => (
        <a href={row.track_url} target="_blank" rel="noopener noreferrer">
          Listen
        </a>
      ),
    },
    { headerName: "Release Date", field: "release_date" },
  ];

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres");
        if (!response.ok) {
          throw new Error("Failed to fetch genres");
        }
        const data = await response.json();
        setGenres(data);
        setSelectedGenre(data[0]?.Genre || "");
      } catch (err) {
        console.error(err);
        setError("Failed to load genres");
      }
    };

    fetchGenres();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!selectedGenre) {
      setError("Please select a genre.");
      return;
    }

    // Update the route for LazyTable
    setRoute(`http://localhost:8080/tracks/genre/${selectedGenre}`);
  };

  return (
    <div>
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div>

      <div id="page-wrapper">
        <form id="search-form" onSubmit={handleSubmit}>
          <label htmlFor="genre-select"></label>

          <select
            id="genre-select"
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            {genres.map((genre) => (
              <option key={genre.ID} value={genre.Genre}>
                {genre.Genre}
              </option>
            ))}
          </select>

          <input
            type="number"
            placeholder="5 results"
            value={term}
            onChange={(e) => setTerm(e.target.value)}
          />

          <button type="submit">Search</button>
        </form>

        {error && <p className="error">{error}</p>}

        {route && (
          <LazyTable
            route={route}
            columns={columns}
            defaultPageSize={5}
            rowsPerPageOptions={[5, 10, 25]}
          />
        )}
      </div>
    </div>
  );
}
