import React, { useState } from "react";
import "../style/SearchPage.css";
import "../index.css";

export default function SearchSong() {
  const [term, setTerm] = useState("");
  const [genre, setGenre] = useState("Fiction"); // Default selected genre
  const [songs, setSongs] = useState([]);
  const [numResults, setNumResults] = useState(0);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSongs([]);
    setNumResults(0);

    if (!genre) {
      setError("Please select a genre.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/tracks/genre/${genre}`);
      if (!response.ok) {
        throw new Error("Failed to fetch data. Please check the genre name.");
      }

      const data = await response.json();
      setSongs(data);
      setNumResults(data.length);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li>Sound Lab</li>
          <li>Genre</li>
          <li>Collaboration</li>
        </ul>
      </div>

      {/* Page Wrapper */}
      <div id="page-wrapper">

        {/* Search Bar */}
        <form id="search-form" onSubmit={handleSubmit} style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <label htmlFor="genre-select">Search:</label>

          {/* Dropdown for Genre Selection */}
          <select
            id="genre-select"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
            style={{ padding: "10px", borderRadius: "4px", border: "1px solid #ccc", width: "100px"}}
          >
            <option value="Fiction">Fiction</option>
            <option value="Business & Economics">Business & Economics</option>
            <option value="Romance">Romance</option>
            <option value="Thrillers">Thrillers</option>
            <option value="Historical">Historical</option>
            <option value="Literary">Literary</option>
            <option value="Suspense">Suspense</option>
          </select>

          {/* Input for Results Limit */}
          <input
            type="number"
            placeholder="5 results"
            value={term}
            onChange={(e) => setTerm(e.target.value)}
            style={{ padding: "10px", borderRadius: "4px", border: "1px solid #ccc", width: "100px" }}
          />

          {/* Search Button */}
          <button type="submit" style={{ padding: "10px 20px", borderRadius: "4px", border: "none", backgroundColor: "#333", color: "white", cursor: "pointer" }}>
            Search
          </button>
        </form>

        {/* Error Message */}
        {error && <p className="error">{error}</p>}

        {/* Results Message */}
        {numResults > 0 && (
          <p>
            Found <span id="num-results">{numResults}</span> result(s).
          </p>
        )}

        {/* Search Results */}
        <div id="search-results">
          {songs.map((song) => (
            <div key={song.track_id} className="song-item">
              <h3>{song.title}</h3>
              <p>Release Date: {song.release_date}</p>
              <a href={song.track_url} target="_blank" rel="noopener noreferrer">
                Listen
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
