import React, { useState, useEffect } from "react";
import "../style/GenrePage.css";
import "../index.css";

export default function GenrePage() {
  const [topGenres, setTopGenres] = useState([]);
  const [artists, setArtists] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(""); // Dropdown for artist name
  const [error, setError] = useState("");

  // Function to fetch artists for the dropdown
  useEffect(() => {
    const fetchArtists = async () => {
      try {
        const response = await fetch("http://localhost:8080/artists");
        if (!response.ok) {
          throw new Error(`Failed to fetch artists. Status: ${response.status}`);
        }
        const data = await response.json();

        // Assuming artists are in `rows`
        if (Array.isArray(data.rows)) {
          setArtists(data.rows);
        } else {
          throw new Error("Invalid response structure: Expected 'rows' to be an array");
        }
      } catch (error) {
        console.error("Error fetching artists:", error);
        setArtistError("Failed to load artists.");
      }
    };

    fetchArtists();
  }, []);

  // Function to fetch top genres for a specific artist
  const fetchTopGenresByArtist = async () => {
    if (!selectedArtist) {
      setError("Please select an artist.");
      return;
    }
    setError("");
    try {
      const response = await fetch(
        `http://localhost:8080/artists/${encodeURIComponent(selectedArtist)}/top-genres`
      );
      if (!response.ok) {
        throw new Error(`Failed to fetch top genres. Status: ${response.status}`);
      }
      const data = await response.json();
      setTopGenres(data);
    } catch (error) {
      console.error("Error fetching top genres for artist:", error);
      setError("Failed to load top genres for artist.");
    }
  };

  // Fetch artists on initial page load
  useEffect(() => {
    fetchArtists();
  }, []);

  return (
    <div id="main_content">
      {/* Fetch Top Genres for a Specific Artist */}
      <div id="top_genres">
        <h2>ðŸŽ¤ Top Genres for Artist</h2>

        <select
          id="artist-select"
          value={selectedArtist}
          onChange={(e) => setSelectedArtist(e.target.value)}
        >
          <option value="" disabled>
            Select an Artist
          </option>
          {Array.isArray(artists) &&
            artists.map((artist) => (
              <option key={artist.ID} value={artist.Artist}>
                {artist.Artist}
              </option>
            ))}
        </select>

        <button onClick={fetchTopGenresByArtist}>Fetch Top Genres</button>

        {topGenres.length > 0 ? (
          <ul>
            {topGenres.map((genre, index) => (
              <li key={index}>
                {genre.genre_name} ({genre.track_count} tracks)
              </li>
            ))}
          </ul>
        ) : (
          <div className="loading-message">No genres found for this artist.</div>
        )}

        {error && <div className="error-message">{error}</div>}
      </div>
    </div>
  );
}
