import React, { useEffect, useState } from "react";

export default function MusicInsights() {
  const [profilesData, setProfilesData] = useState([]); // Data for top audio profiles
  const [loudnessData, setLoudnessData] = useState([]); // Data for loudness-ranked tracks
  const [error, setError] = useState("");

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage] = useState(5); // Number of rows per page

  useEffect(() => {
    const fetchProfilesData = async () => {
      try {
        const response = await fetch("http://localhost:8080/topAudioProfiles");
        if (!response.ok) {
          throw new Error(`Failed to fetch audio profiles. Status: ${response.status}`);
        }
        const profiles = await response.json();
        setProfilesData(profiles);
      } catch (err) {
        console.error("Error fetching audio profiles:", err);
        setError("Failed to load audio profiles by genre.");
      }
    };

    const fetchLoudnessData = async () => {
      try {
        const response = await fetch("http://localhost:8080/rankTracksByLoudness");
        if (!response.ok) {
          throw new Error(`Failed to fetch loudness rankings. Status: ${response.status}`);
        }
        const loudness = await response.json();
        setLoudnessData(loudness);
      } catch (err) {
        console.error("Error fetching loudness rankings:", err);
        setError("Failed to load loudness rankings.");
      }
    };

    fetchProfilesData();
    fetchLoudnessData();
  }, []);

  // Pagination logic
  const paginate = (data) => {
    const start = (currentPage - 1) * rowsPerPage;
    const end = start + rowsPerPage;
    return data.slice(start, end);
  };

  const handlePageChange = (direction) => {
    setCurrentPage((prev) => prev + direction);
  };

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      <h1>Music Insights</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Top Audio Profiles by Genre */}
      <section style={{ marginBottom: "40px" }}>
        <h2>Top Audio Profiles by Genre</h2>
        {profilesData.length > 0 ? (
          <>
            <table border="1" style={{ width: "100%", textAlign: "left", marginBottom: "10px" }}>
              <thead>
                <tr>
                  <th>Genre</th>
                  <th>Valence</th>
                  <th>Energy</th>
                  <th>Danceability</th>
                  <th>Profile Count</th>
                </tr>
              </thead>
              <tbody>
                {paginate(profilesData).map((row, index) => (
                  <tr key={index}>
                    <td>{row.genre_name}</td>
                    <td>{row.valence.toFixed(2)}</td>
                    <td>{row.energy.toFixed(2)}</td>
                    <td>{row.danceability.toFixed(2)}</td>
                    <td>{row.profile_count}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Pagination Controls */}
            <div>
              <button
                disabled={currentPage === 1}
                onClick={() => handlePageChange(-1)}
                style={{ marginRight: "10px" }}
              >
                Previous
              </button>
              <span>
                Page {currentPage} of {Math.ceil(profilesData.length / rowsPerPage)}
              </span>
              <button
                disabled={currentPage === Math.ceil(profilesData.length / rowsPerPage)}
                onClick={() => handlePageChange(1)}
                style={{ marginLeft: "10px" }}
              >
                Next
              </button>
            </div>
          </>
        ) : (
          <p>No data available for audio profiles.</p>
        )}
      </section>

      {/* Loudness Rankings by Genre */}
      <section>
        <h2>Loudness Rankings by Genre</h2>
        {loudnessData.length > 0 ? (
          <>
            <table border="1" style={{ width: "100%", textAlign: "left", marginBottom: "10px" }}>
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Genre</th>
                  <th>Track Title</th>
                  <th>Loudness (dB)</th>
                </tr>
              </thead>
              <tbody>
                {paginate(loudnessData).map((row, index) => (
                  <tr key={index}>
                    <td>{row.loudness_rank}</td>
                    <td>{row.genre_name}</td>
                    <td>{row.title}</td>
                    <td>{row.loudness.toFixed(2)} dB</td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Pagination Controls */}
            <div>
              <button
                disabled={currentPage === 1}
                onClick={() => handlePageChange(-1)}
                style={{ marginRight: "10px" }}
              >
                Previous
              </button>
              <span>
                Page {currentPage} of {Math.ceil(loudnessData.length / rowsPerPage)}
              </span>
              <button
                disabled={currentPage === Math.ceil(loudnessData.length / rowsPerPage)}
                onClick={() => handlePageChange(1)}
                style={{ marginLeft: "10px" }}
              >
                Next
              </button>
            </div>
          </>
        ) : (
          <p>No data available for loudness rankings.</p>
        )}
      </section>
    </div>
  );

  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
