import React, { useState, useEffect } from "react";
import "../style/GenrePage.css";
import "../index.css";

export default function GenrePage() {
  const [songs, setSongs] = useState([]);
  const [genresByID, setGenresByID] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [artists, setArtists] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(""); // Dropdown for artist selection

  // Error states
  const [genreError, setGenreError] = useState("");
  const [artistError, setArtistError] = useState("");

  // Fetch genres by ID on mount
  useEffect(() => {
    const fetchGenresByID = async () => {
      try {
        const response = await fetch("http://localhost:8080/genres-by-id");
        if (!response.ok) {
          throw new Error(`Failed to fetch genres by ID. Status: ${response.status}`);
        }
        const data = await response.json();
        setGenresByID(data); // Assuming genres are directly in the response
      } catch (error) {
        console.error("Error fetching genres by ID:", error);
        setGenreError("Failed to load genres by ID.");
      }
    };

    fetchGenresByID();
  }, []);

  // Fetch artists on mount
  useEffect(() => {
    const fetchArtists = async () => {
      try {
        const response = await fetch("http://localhost:8080/artists");
        if (!response.ok) {
          throw new Error(`Failed to fetch artists. Status: ${response.status}`);
        }
        const data = await response.json();

        // Assuming artists are directly in the response
        if (Array.isArray(data)) {
          setArtists(data);
        } else {
          throw new Error("Invalid response structure: Expected an array of artists.");
        }
      } catch (error) {
        console.error("Error fetching artists:", error);
        setArtistError("Failed to load artists.");
      }
    };

    fetchArtists();
  }, []);

  // Fetch random songs
  const fetchRandomSongs = async () => {
    try {
      const response = await fetch("http://localhost:8080/random-songs?numSongs=5");
      if (!response.ok) {
        throw new Error(`Failed to fetch random songs. Status: ${response.status}`);
      }
      const data = await response.json();
      setSongs(data); // Assuming songs are directly in the response
    } catch (error) {
      console.error("Error fetching random songs:", error);
    }
  };

  // Fetch top genres for a specific artist
  const fetchTopGenresByArtist = async () => {
    if (!selectedArtist) {
      setArtistError("Please select an artist.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8080/artists/${encodeURIComponent(selectedArtist)}/top-genres`
      );
      if (!response.ok) {
        throw new Error(`Failed to fetch top genres. Status: ${response.status}`);
      }
      const data = await response.json();
      setTopGenres(data); // Assuming top genres are directly in the response
    } catch (error) {
      console.error("Error fetching top genres for artist:", error);
      setArtistError("Failed to load top genres for artist.");
    }
  };

  // Fetch random songs on mount
  useEffect(() => {
    fetchRandomSongs();
  }, []);

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li>
            <a href="http://localhost:3000/">HomePage</a>
          </li>
          <li>
            <a href="http://localhost:3000/sound">Sound Lab</a>
          </li>
          <li>
            <a href="http://localhost:3000/genre">GenrePage</a>
          </li>
          <li>
            <a href="http://localhost:3000/collaboration">Collaboration</a>
          </li>
        </ul>
      </div>

      {/* Page Wrapper */}
      <div id="page-wrapper">
        {/* Random Songs Section */}
        <section id="random-songs">
          <h2>ðŸŽµ Random Songs</h2>
          {songs.length > 0 ? (
            <ul>
              {songs.map((song, index) => (
                <li key={index}>
                  {song.title} - {song.genre_name}
                </li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">No songs available.</div>
          )}
        </section>

        {/* Genres by ID Section */}
        <section id="genres-by-id">
          <h2>ðŸ”¢ Genres by ID</h2>
          {genresByID.length > 0 ? (
            <ul>
              {genresByID.map((genre) => (
                <li key={genre.ID}>{genre.Genre}</li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">Loading genres...</div>
          )}
        </section>

        {/* Top Genres for Artist Section */}
        <section id="top-genres">
          <h2>ðŸŽ¤ Top Genres for Artist</h2>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              fetchTopGenresByArtist();
            }}
          >
            <select
              id="artist-select"
              value={selectedArtist}
              onChange={(e) => setSelectedArtist(e.target.value)}
            >
              <option value="" disabled>
                Select an Artist
              </option>
              {artists.map((artist) => (
                <option key={artist.ID} value={artist.Artist}>
                  {artist.Artist}
                </option>
              ))}
            </select>

            <button type="submit">Fetch Top Genres</button>
          </form>

          {topGenres.length > 0 ? (
            <ul>
              {topGenres.map((genre, index) => (
                <li key={index}>
                  {genre.genre_name} ({genre.track_count} tracks)
                </li>
              ))}
            </ul>
          ) : (
            <div className="loading-message">No genres found for this artist.</div>
          )}
        </section>
      </div>
    </div>
  );
}
