import React, { useState, useEffect } from "react";

export default function FeatureSelection() {
  const [songs, setSongs] = useState([]);
  const [features, setFeatures] = useState([]); // Stores feature names dynamically from the API
  const [featureFilters, setFeatureFilters] = useState({}); // Stores slider values for each feature
  const [error, setError] = useState("");

  useEffect(() => {
    // Fetch all feature names dynamically from the API
    const fetchAudioFeatures = async () => {
      try {
        const response = await fetch("http://localhost:8080/getAllAudioFeatures");
        if (!response.ok) {
          throw new Error(`Failed to fetch audio features. Status: ${response.status}`);
        }
        const data = await response.json();

        // Initialize feature filters dynamically
        const initialFilters = {};
        data.forEach((row) => {
          initialFilters[row.column_name] = 0.5; // Default slider value
        });

        setFeatures(data.map((row) => row.column_name)); // Extract feature names
        setFeatureFilters(initialFilters); // Initialize slider values
      } catch (error) {
        console.error("Error fetching audio features:", error);
        setError("Failed to load audio features.");
      }
    };

    fetchAudioFeatures();
  }, []);

  const handleFeatureChange = (feature, value) => {
    setFeatureFilters((prev) => ({
      ...prev,
      [feature]: value,
    }));
  };

  const handleFeatureSearch = async (e) => {
    e.preventDefault();
    setError("");
    setSongs([]);

    try {
      const queryParams = new URLSearchParams(featureFilters);

      const response = await fetch(
        `http://localhost:8080/getTopAudioFeatureProfiles?${queryParams.toString()}`
      );

      if (!response.ok) {
        throw new Error(`Failed to fetch songs by features. Status: ${response.status}`);
      }

      const data = await response.json();
      setSongs(data || []); // Assuming data is an array of songs
    } catch (error) {
      console.error("Error fetching songs by features:", error);
      setError("Failed to load songs by features.");
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/feature-selection">Feature Selection</a></li>
        </ul>
      </div>

      {/* Feature Selection Section */}
      <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start", width: "100%" }}>
        <h2 style={{ marginLeft: "10px" }}>Select Songs by Features</h2>
        <form onSubmit={handleFeatureSearch} style={{ width: "100%" }}>
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(2, 1fr)", // Two sliders per row
              gap: "20px", // Space between items
              width: "90%", // Ensure content stretches left
              marginLeft: "10px", // Move entire grid to the left
            }}
          >
            {features.map((feature) => (
              <div key={feature} style={{ textAlign: "left" }}>
                <label style={{ fontWeight: "bold", textTransform: "capitalize" }}>
                  {feature.replace(/_/g, " ")}
                </label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.01"
                  value={featureFilters[feature] || 0.5}
                  onChange={(e) => handleFeatureChange(feature, parseFloat(e.target.value))}
                  style={{ width: "100%" }}
                />
                <p>{featureFilters[feature]?.toFixed(2)}</p>
              </div>
            ))}
          </div>

          <button type="submit" style={{ marginTop: "20px", marginLeft: "10px" }}>
            Search Songs
          </button>
        </form>
        {error && <p className="error" style={{ color: "red", marginLeft: "10px" }}>{error}</p>}
      </div>

      {/* Songs Display Section */}
      <div className="songs-list" style={{ marginTop: "20px", marginLeft: "10px" }}>
        <h2>Song Results</h2>
        {songs.length > 0 ? (
          songs.map((song) => (
            <div key={song.id} className="song-card">
              <h3>{song.title}</h3>
              <p>Artist: {song.artist}</p>
              <p>Album: {song.album}</p>
              {features.map((feature) => (
                <p key={feature}>
                  {feature.replace(/_/g, " ")}: {song[feature]}
                </p>
              ))}
            </div>
          ))
        ) : (
          <p>No songs found.</p>
        )}
      </div>
    </div>
  );




  return (
    <div>

      {/* Navigation Bar */}
      {/* 不要动 */}
      <div id="navbar">
        <ul>
          <li><a href="http://localhost:3000/">HomePage</a></li>
          <li><a href="http://localhost:3000/search">Search Page</a></li>
          <li><a href="http://localhost:3000/genre">Genre Page</a></li>
          <li><a href="http://localhost:3000/collaboration">Collaboration</a></li>
        </ul>
      </div>

    </div>
  );
}
