import React, { useState } from "react";

export default function FilterSongsByAudioFeatures() {
  const [instrumentalness, setInstrumentalness] = useState(0);
  const [energy, setEnergy] = useState(0);
  const [valence, setValence] = useState(0);
  const [liveness, setLiveness] = useState(0);
  const [acousticness, setAcousticness] = useState(0);
  const [songs, setSongs] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchSongs = async () => {
    try {
      setLoading(true);
      setError(""); // Clear any previous errors

      const queryParams = new URLSearchParams({
        instrumentalness: instrumentalness > 0 ? instrumentalness : undefined,
        energy: energy > 0 ? energy : undefined,
        valence: valence > 0 ? valence : undefined,
        liveness: liveness > 0 ? liveness : undefined,
        acousticness: acousticness > 0 ? acousticness : undefined,
      });

      const response = await fetch(
        `http://localhost:8080/songs-by-audio-features?${queryParams}`
      );

      if (!response.ok) {
        throw new Error(`Failed to fetch songs. Status: ${response.status}`);
      }

      const data = await response.json();
      if (data.rows && data.rows.length > 0) {
        setSongs(data.rows); // Use the `rows` property directly
      } else {
        setSongs([]); // Clear results if no rows found
        setError("No songs found matching the specified criteria.");
      }
    } catch (err) {
      console.error(err);
      setError("Failed to fetch songs. Please try again later.");
    } finally {
      setLoading(false); // Stop loading state
    }
  };

  const clearFilters = () => {
    setInstrumentalness(0);
    setEnergy(0);
    setValence(0);
    setLiveness(0);
    setAcousticness(0);
    setSongs([]);
    setError("");
  };

  return (
    <div>
      <header>
        <h1>Filter Songs by Audio Features</h1>
        <p>Use the sliders below to filter songs based on their audio feature values.</p>
      </header>

      {/* Sliders for filtering */}
      <div className="slider-container" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="instrumentalness">Instrumentalness: {instrumentalness}</label>
          <input
            id="instrumentalness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={instrumentalness}
            onChange={(e) => setInstrumentalness(parseFloat(e.target.value))}
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="energy">Energy: {energy}</label>
          <input
            id="energy"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={energy}
            onChange={(e) => setEnergy(parseFloat(e.target.value))}
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="valence">Valence: {valence}</label>
          <input
            id="valence"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={valence}
            onChange={(e) => setValence(parseFloat(e.target.value))}
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="liveness">Liveness: {liveness}</label>
          <input
            id="liveness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={liveness}
            onChange={(e) => setLiveness(parseFloat(e.target.value))}
          />
        </div>
        <div style={{ flex: "1 1 200px" }}>
          <label htmlFor="acousticness">Acousticness: {acousticness}</label>
          <input
            id="acousticness"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={acousticness}
            onChange={(e) => setAcousticness(parseFloat(e.target.value))}
          />
        </div>
      </div>

      {/* Buttons for search and clear */}
      <div style={{ margin: "20px 0", textAlign: "center" }}>
        <button onClick={fetchSongs} style={{ marginRight: "10px" }}>
          {loading ? "Loading..." : "Search Songs"}
        </button>
        <button onClick={clearFilters} disabled={loading}>
          Clear Filters
        </button>
      </div>

      {/* Results or error */}
      <div className="results">
        {error ? (
          <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>
        ) : songs.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Instrumentalness</th>
                <th>Energy</th>
                <th>Valence</th>
                <th>Liveness</th>
                <th>Acousticness</th>
                <th>URL</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song, index) => (
                <tr key={index}>
                  <td>{song.title}</td>
                  <td>{song.instrumentalness?.toFixed(3)}</td>
                  <td>{song.energy?.toFixed(3)}</td>
                  <td>{song.valence?.toFixed(3)}</td>
                  <td>{song.liveness?.toFixed(3)}</td>
                  <td>{song.acousticness?.toFixed(3)}</td>
                  <td>
                    {song.url ? (
                      <a href={song.url} target="_blank" rel="noopener noreferrer">
                        Listen
                      </a>
                    ) : (
                      "N/A"
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          !loading && <p>No songs to display. Adjust your filters and try again.</p>
        )}
      </div>
    </div>
  );
}
